feeds.query:
{
  allContentfulWriting(
    limit: 1000,
    sort: { order: DESC, fields: [publishDate] }
  ) {
    edges {
      node {
        id
        title
        slug
        publishDate
        tags
        description {
          childMarkdownRemark {
            html
          }
        }
        body {
          childMarkdownRemark {
            html
          }
        }
      }
    }
  }
}

