query WritingBySlug(
  $slug: String!
) {
  contentfulWriting(slug: {eq: $slug}) {
    title
    publishDate(formatString: "MMMM Do, YYYY")
    body {
      childMarkdownRemark {
        html
      }
    }
    update {
      childMarkdownRemark {
        html
      }
    }
    description {
      childMarkdownRemark {
        html
        excerpt
      }
    }
    dateString: publishDate(formatString: "MMMM Do, YYYY")
    extraCss {
      file {
        url
        fileName
        contentType
      }
    }
    extraJs {
      file {
        url
        fileName
        contentType
      }
    }
  }
}

query PageBySlug(
  $slug: String!
) {
  contentfulPage(slug: {eq: $slug}) {
    title
    publishDate(formatString: "MMMM Do, YYYY")
    body {
      childMarkdownRemark {
        html
      }
    }
    description {
      childMarkdownRemark {
        html
        excerpt
      }
    }
    sidebar {
      childMarkdownRemark {
        html
      }
    }
  }
}

query HomeQuery {
  writing: allContentfulWriting(limit: 20, sort: {fields: [publishDate], order: DESC}) {
    edges {
      node {
        id
        title
        slug
        publishDate
        dateString: publishDate(formatString: "MMMM Do, YYYY")
        tags
        description {
          childMarkdownRemark {
            html
          }
        }
        color
        size
      }
